
package com.actuate.wyan;

import java.util.HashMap;

import org.junit.Assert;
import org.junit.Test;

public class DistinctSubsequences
{

	@Test
	public void test( )
	{
		Assert.assertEquals( 0, new Solution( ).numDistinct( "b", "a" ) );
		Assert.assertEquals( 3,
				new Solution( ).numDistinct( "rabbbit", "rabbit" ) );
		Assert.assertEquals(
				8556153,
				new Solution( )
						.numDistinct(
								"daacaedaceacabbaabdccdaaeaebacddadcaeaacadbceaecddecdeedcebcdacdaebccdeebcbdeaccabcecbeeaadbccbaeccbbdaeadecabbbedceaddcdeabbcdaeadcddedddcececbeeabcbecaeadddeddccbdbcdcbceabcacddbbcedebbcaccac",
								"ceadbaa" ) );

		Assert.assertEquals(
				1456742400,
				new Solution( )
						.numDistinct(
								"",
								"rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys" ) );
		Assert.assertEquals(
				0,
				new Solution( )
						.numDistinct(

								"",
								"rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys" ) );
	}

	public class Solution
	{

		public int numDistinct( String S, String T )
		{
			if ( S == null || S.length( ) == 0 )
			{
				return 0;
			}
			if ( T == null || T.length( ) == 0 )
			{
				return 1;
			}
			int count = test2( S, T );
			return count;
		}

		protected void clearCache( )
		{
			runCount = 0;
			cacheCount = 0;
			caches.clear( );
		}
		int runCount = 0;
		int cacheCount = 0;
		HashMap<String, Integer> caches = new HashMap<String, Integer>( );

		int test2( String S, String T )
		{
			clearCache( );
			int first = 0;
			while ( first < S.length( ) )
			{
				if ( S.charAt( first ) != T.charAt( 0 ) )
				{
					first++;
					continue;
				}
				break;
			}

			int count = subsequence( S.toCharArray( ), 0, T.toCharArray( ), 0 );
			return count;
		}

		int subsequence( char[] sources, int offset, char[] targets, int index )
		{
			runCount++;

			if ( index >= targets.length )
			{
				return 1;
			}
			if ( offset >= sources.length )
			{
				return 0;
			}
			String key = offset + "_" + index;
			if ( caches.containsKey( key ) )
			{
				cacheCount++;
				return caches.get( key );
			}

			int count = 0;
			// try match from here
			while ( sources[offset] != targets[index] )
			{
				offset++;
				if ( offset >= sources.length )
				{
					if ( caches.containsKey( key ) )
					{
						cacheCount++;
						return caches.get( key );
					}
					return 0;
				}
			}
			if ( targets[index] == sources[offset] )
			{
				count += subsequence( sources, offset + 1, targets, index + 1 );
			}
			// try match from next
			count += subsequence( sources, offset + 1, targets, index );

			caches.put( key, count );
			return count;
		}
	}
}
